import osimport sysimport video_reportimport user_reportdef main():    print("=" * 70)    print("      抖音自动化举报综合工具箱 (v4.0 CLI版)")    print("=" * 70)    video_report.GlobalCtrl.pause_event = None    video_report.GlobalCtrl.stop_event = None    user_report.GlobalCtrl.pause_event = None    user_report.GlobalCtrl.stop_event = None    path_input = input("Excel绝对路径 (回车默认 D:\\抖音举报.xlsx): ").strip()    g_excel = path_input.replace('"', '').replace("'", "") if path_input else r"D:\抖音举报.xlsx"    choice = input("模式 (1:视频 2:用户 3:混合): ").strip()    v_sheet = "举报指定视频"    u_sheet = "举报指定用户"    try:        if choice == '1':            video_report.main(excel_path=g_excel, sheet_name=v_sheet, auto_exit=True)        elif choice == '2':            user_report.main(excel_path=g_excel, sheet_name=u_sheet, auto_exit=True)        elif choice == '3':            video_report.main(excel_path=g_excel, sheet_name=v_sheet, auto_exit=True)            user_report.main(excel_path=g_excel, sheet_name=u_sheet, auto_exit=True)        else:            print("输入无效")    except Exception as e:        print(f"运行出错: {e}")    input("\n按回车退出...")if __name__ == "__main__":    main()